mapper.select:
SELECT 

FROM shop_product a

WHERE a.id = #{id}

SELECT 

FROM shop_product a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.product_type_id = #{productTypeId}


		AND a.product_name LIKE
			 '%'||#{productName}||'%'
			AND a.product_name LIKE '%'+#{productName}+'%'
			 concat('%',#{productName},'%') 


		AND a.product_char like concat('%',#{pingyinStr},'%')



		AND a.product_no = #{productNo}


		AND a.buy_price = #{buyPrice}


		AND a.unit = #{unit}


		AND a.spec = #{spec}


		AND a.office_id = #{officeId}





		ORDER BY ${page.orderBy}


		ORDER BY a.list_no

SELECT 

FROM shop_product a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.list_no

mapper.update:
UPDATE shop_product SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
