mapper.select:
SELECT 

FROM shop_purchase_supplier a

WHERE a.id = #{id}

SELECT 

FROM shop_purchase_supplier a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.supplier_name = #{supplierName}


		AND a.head_name = #{headName}




		ORDER BY ${page.orderBy}


		ORDER BY a.sort

SELECT 

FROM shop_purchase_supplier a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.sort

mapper.update:
UPDATE shop_purchase_supplier SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
