mapper.select:
SELECT 

FROM shop_supplier_account a

WHERE a.id = #{id}

SELECT 

FROM shop_supplier_account a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.supplier_id = #{supplierId}


		AND a.office_id = #{officeId}


		AND a.order_id = #{orderId}



		AND a.account_no like concat('%',#{accountNo},'%')


		AND a.busin_data BETWEEN #{beginBusinData} AND #{endBusinData}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM shop_supplier_account a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT a.office_id AS "officeId", a.supplier_id AS "supplierId", a.supplier_name AS "supplierName", sum(a.meet_money) as "sumMeetMoney", sum(a.fact_money) as "sumFactMoney", sum(a.less_money) as "sumLessMoney" FROM shop_supplier_account a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.supplier_id = #{supplierId}
AND a.office_id = #{officeId}
AND a.account_no like concat('%',#{accountNo},'%')
AND a.busin_data BETWEEN #{beginBusinData} AND #{endBusinData}
group by a.office_id,a.supplier_id,a.supplier_name

mapper.update:
UPDATE shop_supplier_account SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
