mapper.select:
SELECT 

FROM shop_supplier_account a

WHERE a.id = #{id}

SELECT 

FROM shop_supplier_account a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM shop_supplier_account a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE shop_supplier_account SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
