mapper.select:
SELECT 

FROM vip_user_pay a

WHERE a.id = #{id}

SELECT 

FROM vip_user_pay a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.vip_id = #{vipId}


		AND a.vip_name LIKE 
			'%'||#{vipName}||'%'
			'%'+#{vipName}+'%'
			concat('%',#{vipName},'%')


		AND a.vip_phone = #{vipPhone}


		AND a.office_id = #{officeId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM vip_user_pay a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE vip_user_pay SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
